import { useNavigate } from "react-router-dom";
import { callAPI } from "../App.js";
import { getUsername } from "../pages/login";

export default function DirectButton(props) {
  let navigate = useNavigate();
  const routeChange = (route) => {
    navigate(route);
  };

  return (
    <button
      onClick={() => {     
        var u = getUsername();
        var p = getPassword();
      callAPI(props.route, {u, p}).then(function(r) {
        routeChange(r);
      })
      }}
    >
      {props.text}
    </button>
  );
}
