import React from "react";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContentText from "@material-ui/core/DialogContentText";
import Dialog from "@material-ui/core/Dialog";
import Button from "@material-ui/core/Button";

export default function EditDialog(props) {
  const [open, setOpen] = React.useState(false);
  const [title, setTitle] = React.useState(props.data.title);
  const [description, setDescription] = React.useState(props.data.description);
  const [category, setCategory] = React.useState(props.data.category);
  const [deal, setDeal] = React.useState(props.data.priceReduction);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setOpen(false);
  }

  return (
    <div>
      <Button onClick={handleClickOpen}>EDIT</Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Edit promotion</DialogTitle>
        <DialogContent>
          <DialogContentText>
            <form id="promoform">
              <label>Title: </label>
              <input type="text" value={title} onChange={handleTitleChange}/>
              <br />
              <label>Description: </label>
              <textarea id="description" value={description} onChange={handleDescriptionChange}/>
              <br />
              <label>Category: </label>
              <input type="text"/>
              <br />
              <label>Offer: </label>
              <input type="text"/>
              <label>% off</label>
            </form>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>
            Cancel
          </Button>
          <Button type="submit" form="promoform" onClick={handleSubmit}>
            Submit
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
