import React from "react";

class CategoryEditor extends React.Component {
  constructor(props) {
    super(props);
    //this.handleChange = this.changeHandler.bind(this);

    this.state = {
      category: {
        name: "Burgers",
        items: [
          {
            name: "McSingle",
            description: "description",
            price: 5.0,
          },
          {
            name: "McDouble",
            description: "description",
            price: 6.25,
          },
          {
            name: "McTriple",
            description: "description",
            price: 7.5,
          },
        ],
      },
    };

    this.handleItemNameChange = this.handleItemNameChange.bind(this);
  }

  handleItemNameChange(event)
  {
    this.setState ({[event.target.field]: event.target.value});

  }
23
  render() {
    var i = 0;
    return (
      <div>
        <h3>{this.state.category.name}</h3>
        <table>
          <tbody>
            <tr>
              <th>Item</th>
              <th>Description</th>
              <th>Price</th>
            </tr>
            {this.state.category.items.map((item) => {
              return (
                <tr key={i++}>
                  <td>
                    <input
                      type="input"
                      value={item.name}
                      onChange={this.handleItemNameChange}
                    />
                  </td>
                  <td>
                    <input
                      value={item.description}
                      onChange={this.handleChange}
                    />
                  </td>
                  <td>
                    <input value={item.price} onChange={this.handleChange} />
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

export default CategoryEditor;
